mapper.update:
UPDATE call

SET real_pay = #{realPay}

WHERE call_No = #{callNo}

UPDATE call

SET end_X = #{endX}, end_Y = #{endY}, start_X = #{startX}, start_Y = #{startY}

WHERE call_No = #{callNo}

UPDATE call

SET call_state_code = #{callStateCode}

WHERE call_No = #{callNo}

UPDATE match

SET driver_no = #{driverNo}

WHERE call_No = #{callNo}

mapper.select:
SELECT *

FROM (

SELECT inner_table.*, ROWNUM AS row_seq

FROM (

SELECT c.*

FROM call c

JOIN match m ON c.call_no = m.call_no

WHERE (

m.passenger_no = #{userNo}

OR m.driver_no = #{userNo}

OR m.call_no IN (

SELECT s.call_no

FROM shares s

WHERE s.share_no = #{userNo}

)

)

AND c.call_state_code = '운행후'



AND EXTRACT(MONTH FROM c.call_date) = #{search.searchCondition}



  

ORDER BY c.call_date DESC

) inner_table

WHERE ROWNUM <= #{search.endRowNum}

)

WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT *

FROM (

SELECT inner_table.*, ROWNUM AS row_seq

FROM (

SELECT c.*, m.passenger_no, m.driver_no

FROM call c

JOIN match m ON c.call_no = m.call_no

ORDER BY c.call_date DESC

) inner_table

WHERE ROWNUM <= #{endRowNum}

)

WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT *

FROM (

SELECT inner_table.*, ROWNUM AS row_seq

FROM (

SELECT c.*

FROM call c

JOIN match m ON c.call_no = m.call_no

WHERE (

m.passenger_no = #{userNo}

OR m.driver_no = #{userNo}

)

AND c.call_state_code = '예약중'

  

ORDER BY c.call_date DESC

) inner_table

WHERE ROWNUM <= #{search.endRowNum}

)

WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

SELECT COUNT(*)

FROM (SELECT CALL_NO

from call



AND EXTRACT(MONTH FROM call_date) = #{month}



) countTable
