mapper.update:
UPDATE CALL
	set STAR = #{star}
WHERE CALL_NO = #{callNo}

mapper.select:
select star
from call
where call_no = #{callNo}

select star 
from shares 
where call_no = #{callNo}
and USER_NO = #{passengerNo}

mapper.delete:
DELETE
FROM BLACKLIST
WHERE DRIVER_NO = #{driverNo}
AND PASSENGER_NO = #{passengerNo}

mapper.select:
SELECT *
FROM BLACKLIST
WHERE DRIVER_NO = #{driverNo}
AND PASSENGER_NO = #{passengerNo}

select r.report_no as report_no, r.report_user_no as report_user_no, r.bad_call_no as bad_call_no, r.report_category as report_category, r.report_code as report_code, r.report_detail as report_detail, r.report_date as report_date, r.role as role, r.reg_date as reg_date, p.user_no as USER_NO from report r, pay p where r.bad_call_no = p.call_no and r.bad_call_no = #{badCallNo} and p.pay_type = '실결제' and r.report_no = #{reportNo}
and p.role = 'driver'
and p.role = 'passenger'

  	SELECT *
  	FROM (	SELECT inner_table.* , ROWNUM AS row_seq
  			FROM	(	SELECT *
						FROM REPORT
						
						
							
				 				REPORT_CODE = #{code}
							
							
				 				AND REPORT_CATEGORY = #{searchCondition}
							
							
				 				AND REPORT_USER_NO like #{searchKeyword}||'%'
							
						
						
						ORDER BY REG_DATE ) inner_table
	WHERE ROWNUM <= #{endRowNum} )
	WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT REPORT_CODE
FROM Report
WHERE REPORT_NO = #{reportNo}

