mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT notice_no, notice_Title, TO_CHAR(notice_date, 'YY/MM/DD') as notice_date FROM NOTICE
notice_title LIKE '%'||#{searchKeyword}||'%'
ORDER BY notice_date desc) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
update notice

	notice_title = #{noticeTitle},
	notice_detail = #{noticeDetail}

where notice_no = #{noticeNo}
